import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import Weather from "./Weather";

export default function Search() {
  let [city, setCity] = useState("");

  function handleSubmit(event) {
    event.preventDefault();
    let text = document.querySelector(".text");
    if (city.length <= 0) {
      text.innerHTML = "";
    } else {
      text.innerHTML = `Weather in ${city}`;
    }

  }
  function getWeather(city) {
       let url = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=f96a36c366f556dae54ef30478f423d0&units=metric`;
  let [temperature, setTemperature] = useState(null);
  let [weather, setWeather] = useState("");
  let [humidity, setHumidity] = useState(null);
  let [wind, setWind] = useState();
  let [temperatureMax, setTemperatureMax] = useState(null);
  let [temperatureMin, setTemperatureMin] = useState(null);



  function showTemperature(response) {
    setTemperature(response.data.main.temp);
    setWeather(response.data.weather[0].description);
    setHumidity(response.data.main.humidity);
    setWind(response.data.wind.speed);
    setTemperatureMax(response.data.main.temp_max);
    setTemperatureMin(response.data.main.temp_min);

  }

  axios.get(url).then(showTemperature);

  return (
    <div>
      <ul>
        <li>The temperature is {Math.round(temperature)}°C</li>
        <li>Description: {weather}</li>
        <li>Humidity: {humidity}%</li>
        <li>Wind: {wind} km/h</li>
      </ul>

      <div className="CurrentWeather">
        <h1>{props.place}</h1>
        <h2>date</h2>
        <div className="row justify-content-md-center current-weather align-items-center">
          <div className="col-4">
            <span id="temp">{Math.round(temperature)}</span>
            <a href="#" class="active" id="celsius">
              °C
            </a>
            |
            <a href="#" id="fahrenheit">
              °F
            </a>
          </div>
          <div className="col-4">
            <img
              src="http://openweathermap.org/img/wn/{icon}@2x.png"
              alt="weather.main"
              class="main-icon"
              id="icon"
            />
          </div>
          <div class="col-4" id="weather-sky">
            {weather}
          </div>
        </div>
        <div class="row current-conditions">
          <div class="col-6 text-start">
            <div>
              <i class="bi bi-thermometer-high"></i>
              <span id="temp-max">{temperatureMax}</span>°C
            </div>
            <div>
              <i class="bi bi-thermometer-low"></i>
              <span id="temp-min">{temperatureMin}</span>°C
            </div>
          </div>
          <div class="col-6 text-end">
            <div>
              Wind <i class="bi bi-wind"></i>
            </div>
            <div>
              <span id="wind-speed">{wind}</span> km/h
            </div>
          </div>
        </div>
        <hr />
      </div>
    </div>
  );


  }
  function updateCity(event) {
    event.preventDefault();
    setCity(event.target.value);
  }
  return (
    <div>

      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="Enter city" onChange={updateCity} />
        <input type="submit" value="Search" />
      </form>
      <div className="text"></div>

    </div>
  );
}
